package org.example

import de.vandermeer.asciitable.AsciiTable

fun main() {
    val at = AsciiTable()

    // Añadir encabezados de columna
    at.addRule()
    at.addRow("Nombre", "Edad", "Ciudad")
    at.addRule()

    // Añadir filas
    at.addRow("Juan", "30", "Madrid")
    at.addRow("María", "25", "Barcelona")
    at.addRow("Luis", "35", "Sevilla")
    at.addRule()

    // Imprimir la tabla
    println(at.render())
}
